// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
}

contract SimpleEscrow {
    struct Escrow {
        address buyer;
        address seller;
        address token;
        uint256 amount;
        uint256 escrowFee;
        uint256 networkFee;
        bool buyerConfirmed;
        bool sellerConfirmed;
        bool isReleased;
        bool isRefunded;
        bool isDisputed;
        uint256 createdAt;
        uint256 expiresAt;
    }

    mapping(bytes32 => Escrow) public escrows;
    mapping(address => bool) public authorizedTokens;
    mapping(address => bool) public authorizedOperators;
    
    address public owner;
    uint256 public constant ESCROW_FEE_PERCENT = 100; // 1% (100 basis points)
    uint256 public constant NETWORK_FEE = 100000; // 0.1 USDT (6 decimals)
    uint256 public constant ESCROW_TIMEOUT = 7 days;
    
    event EscrowCreated(bytes32 indexed escrowId, address indexed buyer, address indexed seller, address token, uint256 amount);
    event EscrowConfirmed(bytes32 indexed escrowId, address indexed confirmer);
    event EscrowReleased(bytes32 indexed escrowId, address indexed buyer, uint256 amount);
    event EscrowRefunded(bytes32 indexed escrowId, address indexed seller, uint256 amount);
    event EscrowDisputed(bytes32 indexed escrowId, address indexed disputer);
    event EscrowResolved(bytes32 indexed escrowId, bool released);
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier onlyAuthorizedOperator() {
        require(authorizedOperators[msg.sender], "Not authorized operator");
        _;
    }
    
    modifier validToken(address token) {
        require(authorizedTokens[token], "Token not authorized");
        _;
    }
    
    modifier nonReentrant() {
        // Simple reentrancy guard
        require(!_locked, "Reentrant call");
        _locked = true;
        _;
        _locked = false;
    }
    
    bool private _locked;
    
    constructor() {
        owner = msg.sender;
        authorizedOperators[msg.sender] = true;
    }
    
    function addAuthorizedToken(address token) external onlyOwner {
        authorizedTokens[token] = true;
    }
    
    function addAuthorizedOperator(address operator) external onlyOwner {
        authorizedOperators[operator] = true;
    }
    
    function createEscrow(
        bytes32 escrowId,
        address buyer,
        address seller,
        address token,
        uint256 amount
    ) external onlyAuthorizedOperator validToken(token) {
        require(escrows[escrowId].buyer == address(0), "Escrow already exists");
        require(buyer != address(0) && seller != address(0), "Invalid addresses");
        require(amount > 0, "Amount must be greater than 0");
        
        uint256 escrowFee = (amount * ESCROW_FEE_PERCENT) / 10000;
        
        escrows[escrowId] = Escrow({
            buyer: buyer,
            seller: seller,
            token: token,
            amount: amount,
            escrowFee: escrowFee,
            networkFee: NETWORK_FEE,
            buyerConfirmed: false,
            sellerConfirmed: false,
            isReleased: false,
            isRefunded: false,
            isDisputed: false,
            createdAt: block.timestamp,
            expiresAt: block.timestamp + ESCROW_TIMEOUT
        });
        
        emit EscrowCreated(escrowId, buyer, seller, token, amount);
    }
    
    function confirmEscrow(bytes32 escrowId, bool isBuyer) external {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.buyer != address(0), "Escrow does not exist");
        require(!escrow.isReleased && !escrow.isRefunded, "Escrow already processed");
        require(block.timestamp <= escrow.expiresAt, "Escrow expired");
        
        if (isBuyer) {
            require(msg.sender == escrow.buyer, "Not the buyer");
            require(!escrow.buyerConfirmed, "Already confirmed by buyer");
            escrow.buyerConfirmed = true;
        } else {
            require(msg.sender == escrow.seller, "Not the seller");
            require(!escrow.sellerConfirmed, "Already confirmed by seller");
            escrow.sellerConfirmed = true;
        }
        
        emit EscrowConfirmed(escrowId, msg.sender);
    }
    
    function releaseEscrow(bytes32 escrowId) external onlyAuthorizedOperator nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.buyer != address(0), "Escrow does not exist");
        require(!escrow.isReleased && !escrow.isRefunded, "Already processed");
        require(escrow.buyerConfirmed && escrow.sellerConfirmed, "Not confirmed by both parties");
        
        IERC20 token = IERC20(escrow.token);
        uint256 netAmount = escrow.amount - escrow.networkFee;
        
        require(token.balanceOf(address(this)) >= escrow.amount, "Insufficient balance");
        
        escrow.isReleased = true;
        
        // Transfer to buyer
        require(token.transfer(escrow.buyer, netAmount), "Transfer failed");
        
        // Transfer fees to owner
        if (escrow.escrowFee > 0) {
            require(token.transfer(owner, escrow.escrowFee), "Fee transfer failed");
        }
        
        emit EscrowReleased(escrowId, escrow.buyer, netAmount);
    }
    
    function refundEscrow(bytes32 escrowId) external onlyAuthorizedOperator nonReentrant {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.buyer != address(0), "Escrow does not exist");
        require(!escrow.isReleased && !escrow.isRefunded, "Already processed");
        require(escrow.buyerConfirmed && escrow.sellerConfirmed, "Not confirmed by both parties");
        
        IERC20 token = IERC20(escrow.token);
        uint256 netAmount = escrow.amount - escrow.networkFee;
        
        require(token.balanceOf(address(this)) >= escrow.amount, "Insufficient balance");
        
        escrow.isRefunded = true;
        
        // Transfer to seller
        require(token.transfer(escrow.seller, netAmount), "Transfer failed");
        
        // Transfer fees to owner
        if (escrow.escrowFee > 0) {
            require(token.transfer(owner, escrow.escrowFee), "Fee transfer failed");
        }
        
        emit EscrowRefunded(escrowId, escrow.seller, netAmount);
    }
    
    function disputeEscrow(bytes32 escrowId) external {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.buyer != address(0), "Escrow does not exist");
        require(msg.sender == escrow.buyer || msg.sender == escrow.seller, "Not a party to this escrow");
        require(!escrow.isReleased && !escrow.isRefunded, "Already processed");
        
        escrow.isDisputed = true;
        emit EscrowDisputed(escrowId, msg.sender);
    }
    
    function resolveDispute(bytes32 escrowId, bool release) external onlyOwner {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.buyer != address(0), "Escrow does not exist");
        require(escrow.isDisputed, "Not disputed");
        require(!escrow.isReleased && !escrow.isRefunded, "Already processed");
        require(escrow.buyerConfirmed && escrow.sellerConfirmed, "Not confirmed by both parties");
        
        IERC20 token = IERC20(escrow.token);
        uint256 netAmount = escrow.amount - escrow.networkFee;
        
        require(token.balanceOf(address(this)) >= escrow.amount, "Insufficient balance");
        
        if (release) {
            escrow.isReleased = true;
            require(token.transfer(escrow.buyer, netAmount), "Transfer failed");
            emit EscrowReleased(escrowId, escrow.buyer, netAmount);
        } else {
            escrow.isRefunded = true;
            require(token.transfer(escrow.seller, netAmount), "Transfer failed");
            emit EscrowRefunded(escrowId, escrow.seller, netAmount);
        }
        
        // Transfer fees to owner
        if (escrow.escrowFee > 0) {
            require(token.transfer(owner, escrow.escrowFee), "Fee transfer failed");
        }
        
        emit EscrowResolved(escrowId, release);
    }
    
    function getEscrow(bytes32 escrowId) external view returns (Escrow memory) {
        return escrows[escrowId];
    }
    
    function isEscrowActive(bytes32 escrowId) external view returns (bool) {
        Escrow memory escrow = escrows[escrowId];
        return escrow.buyer != address(0) && 
               !escrow.isReleased && 
               !escrow.isRefunded && 
               block.timestamp <= escrow.expiresAt;
    }
    
    function withdrawFees(address token) external onlyOwner {
        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        require(balance > 0, "No fees to withdraw");
        require(tokenContract.transfer(owner, balance), "Transfer failed");
    }
}

